import React, { useState } from "react";

const Login = () => {
  // üß† State cho form ƒëƒÉng nh·∫≠p
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState(false);

  // üîë H√†m x·ª≠ l√Ω ƒëƒÉng nh·∫≠p
  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage("");
    setError(false);

    // Ki·ªÉm tra input tr·ªëng
    if (!email || !password) {
      setError(true);
      setMessage("‚ö†Ô∏è Vui l√≤ng nh·∫≠p email v√† m·∫≠t kh·∫©u!");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        setMessage(data.message || "‚úÖ ƒêƒÉng nh·∫≠p th√†nh c√¥ng!");
        setError(false);

        // L∆∞u token v√†o localStorage ƒë·ªÉ x√°c th·ª±c
        if (data.token) {
          localStorage.setItem("token", data.token);
        }

        // Reset form
        setEmail("");
        setPassword("");
      } else {
        setError(true);
        setMessage(data.message || "‚ùå ƒêƒÉng nh·∫≠p th·∫•t b·∫°i!");
      }
    } catch (err) {
      setError(true);
      setMessage("üö´ L·ªói k·∫øt n·ªëi ƒë·∫øn server!");
      console.error(err);
    }
  };

  return (
    <div
      style={{
        maxWidth: "400px",
        margin: "50px auto",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "10px",
        boxShadow: "0px 0px 10px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ textAlign: "center" }}>üîê ƒêƒÉng nh·∫≠p</h2>

      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ width: "100%", padding: "8px", margin: "8px 0" }}
        />
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "100%", padding: "8px", margin: "8px 0" }}
        />
        <button
          type="submit"
          style={{
            width: "100%",
            backgroundColor: "#007bff",
            color: "white",
            padding: "10px",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            marginTop: "10px",
          }}
        >
          ƒêƒÉng nh·∫≠p
        </button>
      </form>

      {message && (
        <p
          style={{
            marginTop: "15px",
            color: error ? "red" : "green",
            textAlign: "center",
            fontWeight: "bold",
          }}
        >
          {message}
        </p>
      )}
    </div>
  );
};

export default Login;
