import React, { useState } from "react";

const Register = () => {
  // üß† State l∆∞u gi√° tr·ªã input
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState(false);

  // üì© H√†m x·ª≠ l√Ω ƒëƒÉng k√Ω
  const handleRegister = async (e) => {
    e.preventDefault(); // NgƒÉn reload trang
    setMessage("");
    setError(false);

    // Ki·ªÉm tra input tr·ªëng
    if (!name || !email || !password) {
      setError(true);
      setMessage("‚ö†Ô∏è Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password }),
      });

      const data = await res.json();

      if (res.ok) {
        setMessage(data.message || "‚úÖ ƒêƒÉng k√Ω th√†nh c√¥ng!");
        setError(false);
        // Reset form sau khi ƒëƒÉng k√Ω
        setName("");
        setEmail("");
        setPassword("");
      } else {
        setError(true);
        setMessage(data.message || "‚ùå ƒêƒÉng k√Ω th·∫•t b·∫°i!");
      }
    } catch (err) {
      setError(true);
      setMessage("üö´ L·ªói k·∫øt n·ªëi ƒë·∫øn server!");
      console.error(err);
    }
  };

  return (
    <div
      style={{
        maxWidth: "400px",
        margin: "50px auto",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "10px",
        boxShadow: "0px 0px 10px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ textAlign: "center" }}>üßë‚Äçüíª ƒêƒÉng k√Ω t√†i kho·∫£n</h2>

      <form onSubmit={handleRegister}>
        <input
          type="text"
          placeholder="T√™n ng∆∞·ªùi d√πng"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ width: "100%", padding: "8px", margin: "8px 0" }}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={{ width: "100%", padding: "8px", margin: "8px 0" }}
        />
        <input
          type="password"
          placeholder="M·∫≠t kh·∫©u"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: "100%", padding: "8px", margin: "8px 0" }}
        />
        <button
          type="submit"
          style={{
            width: "100%",
            backgroundColor: "#007bff",
            color: "white",
            padding: "10px",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            marginTop: "10px",
          }}
        >
          G·ª≠i
        </button>
      </form>

      {message && (
        <p
          style={{
            marginTop: "15px",
            color: error ? "red" : "green",
            textAlign: "center",
            fontWeight: "bold",
          }}
        >
          {message}
        </p>
      )}
    </div>
  );
};

export default Register;
